package com.arfdn.disastify.presentation.report

import android.app.Dialog
import android.graphics.Color
import android.graphics.drawable.ColorDrawable
import android.os.Bundle
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.view.Window
import android.widget.Toast
import androidx.recyclerview.widget.LinearLayoutManager
import com.arfdn.disastify.utils.bindFragment
import com.arfdn.disastify.data.model.DisasterDummy
import com.arfdn.disastify.data.model.Geometry
import com.arfdn.disastify.databinding.ListDisasterBottomSheetBinding
import com.google.android.material.bottomsheet.BottomSheetBehavior
import com.google.android.material.bottomsheet.BottomSheetDialog
import com.google.android.material.bottomsheet.BottomSheetDialogFragment

class ListDisasterBottomSheetDialogFragment(val listDisaster:List<Geometry>) : BottomSheetDialogFragment() {

    private val binding by bindFragment(ListDisasterBottomSheetBinding::inflate)
    override fun onCreateDialog(savedInstanceState: Bundle?): Dialog {
        val dialog = super.onCreateDialog(savedInstanceState) as BottomSheetDialog
        dialog.setOnShowListener {
            val bottomSheet = dialog.findViewById<View>(com.google.android.material.R.id.design_bottom_sheet)
            bottomSheet?.let {
                val behavior = BottomSheetBehavior.from(it) // use let to execute the code only if the view is not null
                behavior.state = BottomSheetBehavior.STATE_HALF_EXPANDED
                behavior.isDraggable= true // show 1/3 of the screen by default
                behavior.peekHeight = 100 // remove the default peek height
                behavior.isHideable = false // prevent the user from swiping down to hide the bottom sheet
            }
        }
        return dialog
    }

    override fun onCreateView(inflater: LayoutInflater, container: ViewGroup?, savedInstanceState: Bundle?): View? {
        dialog?.window?.requestFeature(Window.FEATURE_NO_TITLE) // remove the title bar
        dialog?.window?.setBackgroundDrawable(ColorDrawable(Color.TRANSPARENT)) // make the background transparent
        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        val adapterDisaster = DisasterAdapter(listDisaster) { disaster ->
            Toast.makeText(context, "You clicked on ${disaster.properties.createdAt}", Toast.LENGTH_SHORT).show()
        }
        with(binding){
            rvDisaster.apply {
                adapter = adapterDisaster
                layoutManager = LinearLayoutManager(context)
            }
        }
    }

    fun generateDummy(): List<DisasterDummy> {
        val disasters = listOf(
            DisasterDummy(nameDisaster = "Tsunami", disasterType = "Natural", disasterImage = "data:image/jpeg;base64,"),
            DisasterDummy(nameDisaster = "Volcanic Eruption", disasterType = "Natural", disasterImage = "data:image/jpeg;base64,"),
            DisasterDummy(nameDisaster = "Earthquake", disasterType = "Natural", disasterImage = "https://cdn.pixabay.com/photo/2017/09/19/12/38/cracked-2766103_960_720.jpg"),
            DisasterDummy(nameDisaster = "Flood", disasterType = "Natural", disasterImage = "https://cdn.pixabay.com/photo/2017/08/29/04/16/flood-2693980_960_720.jpg"),
            DisasterDummy(nameDisaster = "Fire", disasterType = "Natural", disasterImage = "https://cdn.pixabay.com/photo/2016/08/11/23/48/fire-1587273_960_720.jpg"),
            DisasterDummy(nameDisaster = "Nuclear Accident", disasterType = "Man-made", disasterImage = "https://cdn.pixabay.com/photo/2016/01/19/17/53/nuclear-1149528_960_720.jpg"),
            DisasterDummy(nameDisaster = "Terrorist Attack", disasterType = "Man-made", disasterImage = "https://cdn.pixabay.com/photo/2015/11/06/13/29/explosion-1027898_960_720.jpg"),
            DisasterDummy(nameDisaster = "War", disasterType = "Man-made", disasterImage = "https://cdn.pixabay.com/photo/2014/05/02/21/49/laptop-336373_960_720.jpg")
        )
        return disasters
    }

}